function getHeight(){return window.innerHeight||document.documentElement.clientHeight||document.body.clientHeight}function drawLines(){const e=document.getElementsByClassName("line");if(e.length)for(let n=0;n<e.length;n++)document.body.removeChild(e[n]);for(i=0;i<getHeight()/6;i++){const e=document.createElement("div");e.className="line line-"+i,e.style.top=100*i+"px";const n=5*Math.random();e.style.animation=`lines ${n}s infinite`,document.body.appendChild(e)}}drawLines(),window.onresize=function(e){drawLines()};