const burger=document.querySelector(".burger"),mobileMenu=document.querySelector(".mobile__menu"),burgerClose=document.querySelector(".burger-close");burger.addEventListener("click",()=>{mobileMenu.classList.add("active")}),burgerClose.addEventListener("click",()=>{mobileMenu.classList.remove("active")});const prevScrollPos=window.pageYOffset;window.addEventListener("scroll",(function(){const e=window.pageYOffset;prevScrollPos>e?document.querySelector(".header").classList.remove("hidden"):document.querySelector(".header").classList.add("hidden"),prevScrollPos=e})),document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".filter"),t=document.querySelector(".filter-list"),c=document.querySelector(".sort"),o=document.querySelector(".sort-list");e.addEventListener("click",(function(){t.classList.toggle("active"),o.classList.remove("active")})),c.addEventListener("click",(function(){o.classList.toggle("active"),t.classList.remove("active")})),document.addEventListener("click",(function(r){const s=r.target;s===e||s===c||s===t||s===o||t.contains(s)||o.contains(s)||(t.classList.remove("active"),o.classList.remove("active"))}))}));