const menuBurger=document.querySelector(".header__burger"),mobileNavigation=document.querySelector(".mobile-navigation");menuBurger.addEventListener("click",()=>{mobileNavigation.classList.toggle("active"),menuBurger.classList.toggle("active")});const menu=document.querySelector(".menu-list");menu.addEventListener("click",e=>{e.target.classList.contains("menu-item")&&e.target.classList.contains("sub")&&(document.querySelectorAll(".menu-item.sub.active").forEach(e=>{e.classList.remove("active")}),e.target.classList.add("active"))});